import { useEffect, useRef } from 'react';

const Navbar = ({onProfileClick, onCartClick}) => {
  //const containerRef = useRef(null);

  //useEffect(() => {
  //  if (window.htmx && containerRef.current) {
  //    window.htmx.process(containerRef.current);
  //  }
  //}, []);

  const handleImageError = (e) => {
    e.target.onerror = null; // Prevents infinite loop
    e.target.src = process.env.PUBLIC_URL + '/default_profile.jpg';
  };
  const src = "google.com"

  const cartImg = process.env.PUBLIC_URL + '/Cart.png'


  // ref={containerRef}
  return (
    <div class="fixed top-0 left-0 right-0 bg-white shadow-md z-50 grid grid-cols-3 items-center px-4">

      {/*>burger*/}
      <div>
      </div>
      {/*<burger*/}

      {/*>search bar*/}
      <form action="/action_page.php">
        <label for="fname">First name:</label>
        <input type="text" id="fname" name="fname"/><br/><br/>
        <label for="lname">Last name:</label>
        <input type="text" id="lname" name="lname"/><br/><br/>
        <input type="submit" value="Submit"/>
      </form>
      {/*<search bar*/}

      {/*>clickable elements in a navbar  */}
      <div class="flex justify-end gap-10 px-2">

        <div class="relative">
          <button 
            class="bg-blue-500 rounded-md"
            id="ProfileDropdown"
            onClick={() => onProfileClick()}
          >
            <img 
              type="button" 
              src={src} alt="profile image" 
              class="w-12 h-12 rounded-full object-cover"
              onError={handleImageError}
            />
          </button>
        </div>
        <div class="relative">
          <button 
            class="bg-blue-500 rounded-md" 
            id="CartDropdown"
            onClick={() => onCartClick()}
          >
            <img class="w-14" src={cartImg} />
          </button>
        </div>

      </div>
      {/*<clickable elements in a navbar  */}

    </div>
  );
};

export default Navbar;
