import { useEffect, useRef } from 'react';

const Demo1 = () => {

  const containerRef = useRef(null);
  useEffect(() => {
    if (window.htmx && containerRef.current) {
      window.htmx.process(containerRef.current);
    }
  }, []);

  return (
    <div ref={containerRef}>

      <h2>HTMX with web component</h2>
      <p id="content">REPLACE ME</p>
      <button class="button"
        id="load-button"
        hx-get="/data/vars-1.html"
        hx-trigger="click"
        hx-target="#content"
      >Load New Content</button>

      <button 
        id="add-btn"
        hx-get="/data/vars-1.html"
        hx-trigger="click"
        hx-target="#item-list"
      >Interact with another component</button>
    </div>
  );
}


export default Demo1;
