import Navbar from './Navbar';
import DropDowns from './DropDowns';
import './Main.css';
import { useState, useRef, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';

const Main = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  // const num = searchParams.get('num') ?? '0';;

  const ref_productContainer = useRef(null);

  //const ref_dropDownOutside = useRef(null);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false);
    useEffect(() => {
      if (!searchParams.has('num')) {
        // Clone the current search params, add default num
        const newParams = new URLSearchParams(searchParams);
        newParams.set('num', '0');
        navigate(`/?${newParams.toString()}`, { replace: true }); // `replace` avoids history jump
      }
      // TODO IT BRAKES WHEN NEW CONTENT IS LOADED FROM HTMX
      // hide DropDowns when user clicks on somewhere outside of the buttons
      //const handleClickOutside = (event) => {
      //  // if click was outside of "ref_dropDownOutside" DOM node
      //  if (ref_dropDownOutside.current && !ref_dropDownOutside.current.contains(event.target)) {
      //    setIsProfileOpen(false);
      //    setIsCartOpen(false);
      //  }
      //};
      //document.addEventListener('mousedown', handleClickOutside);

      // htmx related
      if (window.htmx && ref_productContainer.current) {
        window.htmx.process(ref_productContainer.current);
      }

      // when component unmounts
      //return () => {
      //  document.removeEventListener('mousedown', handleClickOutside);
      //};
    }, [ref_productContainer]);// do ones when node mounts to DOM

  var num = searchParams.get('num');

  const mainLink = "http://localhost:25000/main?start=0&num="+num


//  useEffect(() => {
//    // This assumes you get the session ID from props, context, or a global variable
//    const token = window.SESSION_ID; // Or passed as a prop/context
//
//    if (token) {
//      document.cookie = `session=${token}`;
//    }
//
//    // Update the browser history
//    window.history.pushState({}, '', '/');
//  }, []);

  return (
  <div id="DivBody" className="bg-gray-100 text-gray-900">
    {/* change target for search, add a profile img */}
    <Navbar
      onProfileClick={()=>{
        setIsCartOpen(false);
        setIsProfileOpen((prev) => !prev);
        }
      }
      onCartClick={()=>{
        setIsProfileOpen(false);
        setIsCartOpen((prev) => !prev)
        }
      }
    />
    <DropDowns 
      isProfileOpen={isProfileOpen}
      isCartOpen={isCartOpen}
    />
    <div ref={ref_productContainer} id="contentcontainer" class="contentcontainer p-6 mt-5">
      <div id="products" class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 p-4">
        {/*ADD JS that will change the num is this reguest*/}
        <div 
          hx-trigger="revealed"
          hx-swap="outerHTML"
          hx-get={mainLink}
          className="text-center p-4 bg-gray-200 rounded-lg shadow-md hover:bg-gray-300 transition"
        ></div>
      </div>
      <div id="nextpage" className="col-auto row-auto">
      </div>
    </div>

  </div>
  );
};

export default Main;
