import './Main.css';
import { useEffect, useRef } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';

const Main = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const productContainerRef = useRef(null);

  useEffect(() => {
    if (!searchParams.has('num')) {
      const newParams = new URLSearchParams(searchParams);
      newParams.set('num', '0');
      navigate(`/?${newParams.toString()}`, { replace: true }); // `replace` avoids history jump
    }

    if (window.htmx && productContainerRef.current) {
      // Replace the inner HTMX div to re-trigger HTMX
      productContainerRef.current.innerHTML = `
        <div 
          hx-get="${mainLink}"
          hx-trigger="load"
          hx-swap="innerHTML"
          class="text-center p-4 bg-gray-200 rounded-lg shadow-md hover:bg-gray-300 transition"
        ></div>
      `;
      window.htmx.process(productContainerRef.current);
    }
  }, [search]);

  var num = searchParams.get('num');
  var search = searchParams.get('search') ?? '';

  const mainLink = search === ''
    ? `http://localhost:25000/main?start=0&num=${num}`
    : `http://localhost:25000/main?start=0&search=${encodeURIComponent(search)}&num=${num}`;

  return (
    <div>
      <div id="products" key={search} class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 p-4">
        {/*ADD JS that will change the num is this reguest*/}
        <div 
          hx-trigger="revealed"
          hx-swap="outerHTML"
          hx-get={mainLink}
          className="text-center p-4 bg-gray-200 rounded-lg shadow-md hover:bg-gray-300 transition"
        ></div>
      </div>
      <div id="nextpage" className="col-auto row-auto">
          {/* will have oob content put here from API */}
      </div>
    </div>
  );
};

export default Main;
