import { useEffect, useRef } from 'react';

const Navbar = (onProfileClick, onCartClick) => {
  const containerRef = useRef(null);

  useEffect(() => {
    if (window.htmx && containerRef.current) {
      window.htmx.process(containerRef.current);
    }
  }, []);


  return (
    <div ref={containerRef} className="fixed top-0 left-0 right-0 bg-white shadow-md z-50 grid grid-cols-3 items-center px-4">

      {/*>burger*/}
      <div>
        put burger menu &
        put logo here
      </div>
      {/*<burger*/}

      {/*>search bar*/}
      <div id="searchbar" className="flex justify-center">
          {/*hx-target="#products"*/}
        <input 
          className="w-80"
          type="search"
          name="search"
          placeholder="Type To Search"
          hx-target="#temp"
          hx-get="/search?newSearch=1"
          hx-trigger="input changed"
          hx-indicator=".htmx-indicator"
          hx-swap="innerHTML"/>
        <div id="temp" className="htmx-indicator mt-5 absolute text-green-600 font-semibold ">Searching...</div>
      </div>
      {/*<search bar*/}

      {/*>clickable elements in a navbar  */}
      <div className="flex justify-end gap-10 px-2">

        <div className="relative">
          <button 
            className="bg-blue-500 rounded-md"
            id="ProfileDropdown"
            onClick={() => onProfileClick()}
          >
            <img 
              type="button" src="" 
              alt="profile image" className="w-12 h-12 rounded-full object-cover"
              onerror="this.onerror=null;this.src='https://img.freepik.com/free-vector/blue-circle-with-white-user_78370-4707.jpg?t=st=1741218983~exp=1741222583~hmac=1b0ea872dd8d4b7b578200204a9df957dd072b79cd6b9644780d786ed6756b2b&w=740'"/>
          </button>
        </div>
        <div className="relative">
          <button 
            className="bg-blue-500 rounded-md" 
            id="CartDropdown"
            onClick={() => onCartClick()}
          >
            <img className="w-14" src="https://www.pngmart.com/files/7/Cart-PNG-Transparent-Image.png"/>
          </button>
        </div>

      </div>
      {/*<clickable elements in a navbar  */}

    </div>
  );
};

export default Navbar;
