//    window.history.pushState({}, '', '/');
//  }, []);

  return (
  <div id="DivBody" ref={ref_dropDownOutside} className="bg-gray-100 text-gray-900">
    {/* change target for search, add a profile img */}
    <Navbar
      onProfileClick={()=>{
        setIsCartOpen(false);
        setIsProfileOpen((prev) => !prev);
        }
