import { useEffect, useRef } from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = ({onProfileClick, onCartClick}) => {
  const containerRef = useRef(null);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (window.htmx && containerRef.current) {
      window.htmx.process(containerRef.current);
    }
  }, []);

  const handleImageError = (e) => {
    e.target.onerror = null; // Prevents infinite loop
    e.target.src = process.env.PUBLIC_URL + '/default_profile.jpg';
  };
  const src = "google.com"

  const cartImg = process.env.PUBLIC_URL + '/Cart.png'

  const handleKeyUp = (e) => {
    if (e.key === 'Enter') {
      // Update URL with new search parameter
      const params = new URLSearchParams(window.location.search);
      params.set('search', searchTerm);
      navigate(`?${params.toString()}`);

      // You might also trigger a fetch or update local state here
    }
  };


  return (
    <div ref={containerRef} className="fixed top-0 left-0 right-0 bg-white shadow-md z-50 grid grid-cols-3 items-center px-4">

      {/*>burger*/}
      <div>
        put burger menu
        <a href='/?num=0'>
        put logo here
        </a>
      </div>
      {/*<burger*/}

      {/*>search bar*/}
      <div id="searchbar" className="flex justify-center">
        {/*hx-target="#products"*/}
        <input
          className="w-80"
          type="search"
          name="search"
          placeholder="Type To Search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyUp={handleKeyUp}
        />
      </div>
      {/*<search bar*/}

      {/*>clickable elements in a navbar  */}
      <div className="flex justify-end gap-10 px-2">

        <div className="relative">
          <button 
            className="bg-blue-500 rounded-md"
            id="ProfileDropdown"
            onClick={() => onProfileClick()}
          >
            <img 
              type="button" 
              src={src} alt="profile image" 
              className="w-12 h-12 rounded-full object-cover"
              onError={handleImageError}
            />
          </button>
        </div>
        <div className="relative">
          <button 
            className="bg-blue-500 rounded-md" 
            id="CartDropdown"
            onClick={() => onCartClick()}
          >
            <img className="w-14" src={cartImg} />
          </button>
        </div>

      </div>
      {/*<clickable elements in a navbar  */}

    </div>
  );
};

export default Navbar;
